"use strict";

/*
	implementacion personal de algo que podria ya existir
	un monitor de todas las operaciones de red y de base de datos
	asi como miscelaneas
	pero seria preciso por cada operacion invocar un metodo de este modulo
*/

module.exports = {
	httpRequest : function(data){
		console.log("----------");
		var method;
		for(var v in data.route.methods)
			method = v;
		console.log(method.toUpperCase() + " " + data.originalUrl);
		console.log("Logged: " + (data.session.logged?"yes":"no"));
		if(data.session.logged){
			console.log("ID-user: " + data.session.iduser);
			console.log("ID-session-DB: " + data.session.id_session_db);
		}
	}
}